import styled from 'styled-components';


export const InputContainer = styled.div`
    width: 100%;
    height: 75px;
    background-color: #D3D3D3;

    display: flex;
    align-items: center;
    justify-content: flex end;
    font-size: 24px;

    input {
        width: 100%;
        height: 75px;
        background-color: #00000;
        border: 0;
        padding: 0 10px;
        font-size: 24px;
        color: #FFFFFF;
    }
`
