import styled from 'styled-components';


export const ButtonContainer = styled.button`
    padding: 20px;
    border: 1px solid #000;
    background-color: #000;
    font-size: 24px;
    font-weight: 700;
    color: #000;
    flex: 1;

    &:hover {
        opacity: 0.6;
    }
`
